package Assignment5_ForLoop;

public class Question12_numbersNestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=10;
		
		
		for(int i =1; i<=number; i++) {
			for(int j=1; j<=10; j++) {
				System.out.print((i*j)+" ");						
					
				}
			System.out.println();
			}
			
		}

	}

