package ReviewWeek4;

public class CountingDown {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//WHILE LOOP
		
		int x=10;
		
		while(x>=0) {
			System.out.println(x);
			x--;
			
		}
		
		// DO WHILE
		
		int y=10;
		
		do {
			System.out.println(y);
			y--;
			} while(y>=0);
		
		//FOR LOOP
		
		for (int z=10; z>=0; z--) {
			System.out.println(z);
		}
			

	}

}
