package day07_IfSatetements;

public class task27_GoodMorning {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int hour=14;
		
		if(hour<12) {
			System.out.println("Good Morning");
		}
		if(hour>=12 && hour<15) {
			System.out.println("Good Morning");
		}
		if(hour>=15) {
			System.out.println("Good Morning");
		}
	}

}
