package day09_summaryOfflowStatements;

public class SwitchRules {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String lastName="Smith";
		String firstName="Mike";
		int id=0;
		
		switch(firstName) {
		
		case "Test":
			System.out.println("Test");
		case lastName:
			id=5;
			break;
			
		}

	}

}
